import React, { useState, useEffect } from 'react';
import { getStates, addState, updateState, deleteState, getCountries } from '../api';

const State = () => {
  const [states, setStates] = useState([]);
  const [countries, setCountries] = useState([]);
  const [newStateName, setNewStateName] = useState('');
  const [newStateCountryId, setNewStateCountryId] = useState('');
  const [editStateId, setEditStateId] = useState(null);
  const [editStateName, setEditStateName] = useState('');
  const [editStateCountryId, setEditStateCountryId] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchStates = async () => {
      try {
        const data = await getStates();
        setStates(data);
      } catch (error) {
        console.error('Error fetching states:', error);
      }
    };

    const fetchCountries = async () => {
      try {
        const data = await getCountries();
        setCountries(data);
      } catch (error) {
        console.error('Error fetching countries:', error);
      }
    };

    fetchStates();
    fetchCountries();
  }, []);

  const handleAddState = async (e) => {
    e.preventDefault();
    try {
      const newState = await addState(newStateName, newStateCountryId);
      setStates([...states, newState]);
      setNewStateName('');
      setNewStateCountryId('');
    } catch (error) {
      console.error('Error adding state:', error);
    }
  };

  const handleUpdateState = async (id) => {
    try {
      const updatedState = await updateState(id, editStateName, editStateCountryId);
      setStates(states.map(state => (state._id === id ? updatedState : state)));
      setEditStateId(null);
      setEditStateName('');
      setEditStateCountryId('');
    } catch (error) {
      console.error('Error updating state:', error);
    }
  };

  const handleDeleteState = async (id) => {
    try {
      await deleteState(id);
      setStates(states.filter(state => state._id !== id));
    } catch (error) {
      console.error('Error deleting state:', error);
    }
  };

  const getCountryName = (countryId) => {
    const country = countries.find(country => country._id === countryId);
    return country ? country.name : '';
  };

  const filteredStates = states.filter(state => 
    state.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
    getCountryName(state.countryId).toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="container">
      <style jsx>{`
        .container {
          width: 600px; /* Adjusted width to 600px */
          margin: 20px auto;
          padding: 50px;
          background-color: #fff;
          border-radius: 8px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        h2 {
          text-align: center;
          color: #333;
          margin-bottom: 20px;
        }

        .search {
          margin-bottom: 20px;
        }

        .search input {
          width: 100%;
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 4px;
        }

        form {
          display: flex;
          flex-direction: column;
          gap: 10px;
          margin-bottom: 20px;
        }

        form input[type="text"],
        form select {
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 4px;
        }

        form button {
          padding: 12px 24px; /* Increased padding for buttons */
          background-color: #28a745;
          color: white;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }

        form button:hover {
          background-color: #218838;
        }

        ul {
          list-style: none;
          padding: 0;
        }

        li {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 10px;
          background-color: #f8f9fa;
          border: 1px solid #ddd;
          border-radius: 4px;
          margin-bottom: 10px;
        }

        li:nth-child(even) {
          background-color: #e9ecef;
        }

        .edit-container,
        .state-details {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .state-details {
          flex-grow: 1;
          display: flex;
          justify-content: space-between;
        }

        .edit-button,
        .delete-button {
          padding: 12px 24px; /* Increased padding for buttons */
          font-size: 14px;
          border-radius: 4px;
        }

        .edit-button {
          background-color: #007bff;
          color: white;
        }

        .edit-button:hover {
          background-color: #0056b3;
        }

        .delete-button {
          background-color: #dc3545;
          color: white;
        }

        .delete-button:hover {
          background-color: #c82333;
        }

        .input-fullwidth {
          width: 100%;
        }
      `}</style>

      <h2>States</h2>
      <div className="search">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search states or countries"
        />
      </div>
      <form onSubmit={handleAddState}>
        <input
          type="text"
          value={newStateName}
          onChange={(e) => setNewStateName(e.target.value)}
          placeholder="Add new state"
          required
        />
        <select value={newStateCountryId} onChange={(e) => setNewStateCountryId(e.target.value)} required>
          <option value="">Select Country</option>
          {countries.map(country => (
            <option key={country._id} value={country._id}>{country.name}</option>
          ))}
        </select>
        <button type="submit">Add State</button>
      </form>
      <ul>
        {filteredStates.map(state => (
          <li key={state._id}>
            {editStateId === state._id ? (
              <div className="edit-container">
                <input
                  type="text"
                  value={editStateName}
                  onChange={(e) => setEditStateName(e.target.value)}
                  className="input-fullwidth"
                />
                <select
                  value={editStateCountryId}
                  onChange={(e) => setEditStateCountryId(e.target.value)}
                  className="input-fullwidth"
                >
                  <option value="">Select Country</option>
                  {countries.map(country => (
                    <option key={country._id} value={country._id}>{country.name}</option>
                  ))}
                </select>
                <button className="edit-button" onClick={() => handleUpdateState(state._id)}>Save</button>
                <button className="delete-button" onClick={() => setEditStateId(null)}>Cancel</button>
              </div>
            ) : (
              <div className="state-details">
                <span>{state.name} - {getCountryName(state.countryId)}</span>
                <div>
                  <button className="edit-button" onClick={() => {
                    setEditStateId(state._id);
                    setEditStateName(state.name);
                    setEditStateCountryId(state.countryId);
                  }}>Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteState(state._id)}>Delete</button>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default State;
