import React, { useState, useEffect } from 'react';
import { getCities, addCity, updateCity, deleteCity, getStates } from '../api';

const City = () => {
  const [cities, setCities] = useState([]);
  const [states, setStates] = useState([]);
  const [newCityName, setNewCityName] = useState('');
  const [newCityStateId, setNewCityStateId] = useState('');
  const [editCityId, setEditCityId] = useState(null);
  const [editCityName, setEditCityName] = useState('');
  const [editCityStateId, setEditCityStateId] = useState('');

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const data = await getCities();
        setCities(data);
      } catch (error) {
        console.error('Error fetching cities:', error);
      }
    };

    const fetchStates = async () => {
      try {
        const data = await getStates();
        setStates(data);
      } catch (error) {
        console.error('Error fetching states:', error);
      }
    };

    fetchCities();
    fetchStates();
  }, []);

  const handleAddCity = async (e) => {
    e.preventDefault();
    try {
      const newCity = await addCity(newCityName, newCityStateId);
      setCities([...cities, newCity]);
      setNewCityName('');
      setNewCityStateId('');
    } catch (error) {
      console.error('Error adding city:', error);
    }
  };

  const handleUpdateCity = async (id) => {
    try {
      const updatedCity = await updateCity(id, editCityName, editCityStateId);
      setCities(cities.map(city => (city._id === id ? updatedCity : city)));
      setEditCityId(null);
      setEditCityName('');
      setEditCityStateId('');
    } catch (error) {
      console.error('Error updating city:', error);
    }
  };

  const handleDeleteCity = async (id) => {
    try {
      await deleteCity(id);
      setCities(cities.filter(city => city._id !== id));
    } catch (error) {
      console.error('Error deleting city:', error);
    }
  };

  return (
    <div className="city-container">
      <style>{`
        .city-container {
          max-width: 600px;
          margin: auto;
          padding: 20px;
          background-color: #f9f9f9;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        h2 {
          text-align: center;
          color: #333;
          margin-bottom: 20px;
        }
        form {
          display: flex;
          flex-direction: column;
          gap: 10px;
        }
        input, select {
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 5px;
        }
        button {
          padding: 10px;
          background-color: #007bff;
          color: #fff;
          border: none;
          border-radius: 5px;
          cursor: pointer;
        }
        button:hover {
          background-color: #0056b3;
        }
        ul {
          list-style-type: none;
          padding: 0;
          margin-top: 20px;
        }
        li {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 10px;
          border-bottom: 1px solid #ccc;
        }
        li span {
          flex-grow: 1;
        }
        li div {
          display: flex;
          gap: 5px;
        }
        .edit-button {
          background-color: #ffc107;
        }
        .edit-button:hover {
          background-color: #e0a800;
        }
        .delete-button {
          background-color: #dc3545;
        }
        .delete-button:hover {
          background-color: #c82333;
        }
        .save-button {
          background-color: #28a745;
        }
        .save-button:hover {
          background-color: #218838;
        }
        .cancel-button {
          background-color: #6c757d;
        }
        .cancel-button:hover {
          background-color: #5a6268;
        }
      `}</style>
      <h2>Cities</h2>
      <form onSubmit={handleAddCity}>
        <input
          type="text"
          value={newCityName}
          onChange={(e) => setNewCityName(e.target.value)}
          placeholder="Add new city"
          required
        />
        <select value={newCityStateId} onChange={(e) => setNewCityStateId(e.target.value)} required>
          <option value="">Select State</option>
          {states.map(state => (
            <option key={state._id} value={state._id}>{state.name}</option>
          ))}
        </select>
        <button type="submit">Add City</button>
      </form>
      <ul>
        {cities.map(city => (
          <li key={city._id}>
            {editCityId === city._id ? (
              <>
                <input
                  type="text"
                  value={editCityName}
                  onChange={(e) => setEditCityName(e.target.value)}
                />
                <select
                  value={editCityStateId}
                  onChange={(e) => setEditCityStateId(e.target.value)}
                >
                  <option value="">Select State</option>
                  {states.map(state => (
                    <option key={state._id} value={state._id}>{state.name}</option>
                  ))}
                </select>
                <button className="save-button" onClick={() => handleUpdateCity(city._id)}>Save</button>
                <button className="cancel-button" onClick={() => setEditCityId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <span>{city.name} - {city.stateId}</span>
                <div>
                  <button className="edit-button" onClick={() => {
                    setEditCityId(city._id);
                    setEditCityName(city.name);
                    setEditCityStateId(city.stateId);
                  }}>Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteCity(city._id)}>Delete</button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default City;
