import React, { useState, useEffect } from 'react';
import { getCities, addCity, updateCity, deleteCity, getStates } from '../api';
import styled from 'styled-components';

const CityPage = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  font-family: Arial, sans-serif;
`;

const Title = styled.h2`
  font-size: 2rem;
  margin-bottom: 20px;
  text-align: center;
  color: #333;
`;

const Form = styled.form`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const Input = styled.input`
  padding: 10px;
  margin-right: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 1rem;
`;

const Select = styled.select`
  padding: 10px;
  margin-right: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 1rem;
`;

const Button = styled.button`
  padding: 10px;
  margin-right: 10px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.3s;
  
  &.add {
    background-color: #28a745;
    color: white;

    &:hover {
      background-color: #218838;
    }
  }

  &.edit {
    background-color: #007bff;
    color: white;

    &:hover {
      background-color: #0056b3;
    }
  }

  &.delete {
    background-color: #dc3545;
    color: white;

    &:hover {
      background-color: #c82333;
    }
  }

  &.save {
    background-color: #28a745;
    color: white;

    &:hover {
      background-color: #218838;
    }
  }

  &.cancel {
    background-color: #6c757d;
    color: white;

    &:hover {
      background-color: #5a6268;
    }
  }
`;

const List = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const ListItem = styled.li`
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const City = () => {
  const [cities, setCities] = useState([]);
  const [states, setStates] = useState([]);
  const [newCityName, setNewCityName] = useState('');
  const [newCityStateId, setNewCityStateId] = useState('');
  const [editCityId, setEditCityId] = useState(null);
  const [editCityName, setEditCityName] = useState('');
  const [editCityStateId, setEditCityStateId] = useState('');

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const data = await getCities();
        setCities(data);
      } catch (error) {
        console.error('Error fetching cities:', error);
      }
    };

    const fetchStates = async () => {
      try {
        const data = await getStates();
        setStates(data);
      } catch (error) {
        console.error('Error fetching states:', error);
      }
    };

    fetchCities();
    fetchStates();
  }, []);

  const handleAddCity = async (e) => {
    e.preventDefault();
    try {
      const newCity = await addCity(newCityName, newCityStateId);
      setCities([...cities, newCity]);
      setNewCityName('');
      setNewCityStateId('');
    } catch (error) {
      console.error('Error adding city:', error);
    }
  };

  const handleUpdateCity = async (id) => {
    try {
      const updatedCity = await updateCity(id, editCityName, editCityStateId);
      setCities(cities.map(city => (city._id === id ? updatedCity : city)));
      setEditCityId(null);
      setEditCityName('');
      setEditCityStateId('');
    } catch (error) {
      console.error('Error updating city:', error);
    }
  };

  const handleDeleteCity = async (id) => {
    try {
      await deleteCity(id);
      setCities(cities.filter(city => city._id !== id));
    } catch (error) {
      console.error('Error deleting city:', error);
    }
  };

  return (
    <CityPage>
      <Title>Cities</Title>
      <Form onSubmit={handleAddCity}>
        <Input
          type="text"
          value={newCityName}
          onChange={(e) => setNewCityName(e.target.value)}
          placeholder="Add new city"
          required
        />
        <Select value={newCityStateId} onChange={(e) => setNewCityStateId(e.target.value)} required>
          <option value="">Select State</option>
          {states.map(state => (
            <option key={state._id} value={state._id}>{state.name}</option>
          ))}
        </Select>
        <Button type="submit" className="add">Add City</Button>
      </Form>
      <List>
        {cities.map(city => (
          <ListItem key={city._id}>
            {editCityId === city._id ? (
              <>
                <Input
                  type="text"
                  value={editCityName}
                  onChange={(e) => setEditCityName(e.target.value)}
                />
                <Select
                  value={editCityStateId}
                  onChange={(e) => setEditCityStateId(e.target.value)}
                >
                  <option value="">Select State</option>
                  {states.map(state => (
                    <option key={state._id} value={state._id}>{state.name}</option>
                  ))}
                </Select>
                <Button className="save" onClick={() => handleUpdateCity(city._id)}>Save</Button>
                <Button className="cancel" onClick={() => setEditCityId(null)}>Cancel</Button>
              </>
            ) : (
              <>
                {city.name} - {city.stateId}
                <div>
                  <Button className="edit" onClick={() => {
                    setEditCityId(city._id);
                    setEditCityName(city.name);
                    setEditCityStateId(city.stateId);
                  }}>Edit</Button>
                  <Button className="delete" onClick={() => handleDeleteCity(city._id)}>Delete</Button>
                </div>
              </>
            )}
          </ListItem>
        ))}
      </List>
    </CityPage>
  );
};

export default City;
